mapper.select:
SELECT	COUNT(COMMENTNUM)  
FROM	COMMENTS           
WHERE	BOARDNUM = #{boardNum}

SELECT *
FROM (
    SELECT ROWNUM r, c.*, m.PROFILEPATH  
    FROM COMMENTS c 
    JOIN MEMBER m ON c.MEMBERNUM = m.MEMBERNUM  
    WHERE c.BOARDNUM = #{commentsDTO.boardNum}  
    ORDER BY c.CREATEDATE DESC, c.UPDATEDATE DESC  
	)
WHERE r BETWEEN #{scroller.startRow} AND #{scroller.endRow}

mapper.update:
UPDATE REPLY
SET CONTENT = #{content}, UPDATEDATE = SYSDATE	
WHERE REPLYNUM = #{replyNum} AND MEMBERNUM = #{memberNum}

UPDATE COMMENTS
SET CONTENT = #{content}, UPDATEDATE = SYSDATE  
WHERE COMMENTNUM = #{commentNum} AND MEMBERNUM = #{memberNum}
