mapper.update:
UPDATE TRAVEL
SET
CHILDBOARD=#{boardNum}
WHERE BOARDNUM=#{parentBoard}

mapper.select:
SELECT T.*, TO_CHAR(CREATEDATE, 'YYYY"년" MM"월" DD"일"') AS writeDate
FROM TRAVEL T
WHERE BOARDNUM=#{boardNum}

mapper.update:
UPDATE TRAVEL
SET HIT = HIT + 1
WHERE BOARDNUM = #{boardNum}

UPDATE TRAVEL
SET BOARDTITLE = #{boardTitle},
	BOARDCONTENT = #{boardContent},
	UPDATEDATE = SYSDATE,
	FILENAME = #{fileName, jdbcType=VARCHAR}
WHERE BOARDNUM = #{boardNum}

UPDATE TRAVEL
SET CHILDBOARD=#{childBoard}
WHERE BOARDNUM=#{parentBoard}

UPDATE TRAVEL
SET PARENTBOARD=#{parentBoard}
WHERE BOARDNUM=#{childBoard}

UPDATE TRAVEL
SET CHILDBOARD=NULL
WHERE BOARDNUM=#{parentBoard}

mapper.select:
SELECT COMMENTNUM
FROM COMMENTS
WHERE BOARDNUM = ${boardNum}

SELECT PINGNUM, BOARDNUM
FROM TRAVEL
WHERE ROOTBOARD = #{rootBoard}
